$:/aphorism-styles.text:
.xp-aphorism {
    width: 100%;
}

.xp-aphorism table,
.xp-aphorism tr,
.xp-aphorism td {
    border: none;
    padding: 5px;
}

.xp-aphorism-item .xp-aphorism-star tr {
    border-left: 2px inset <<colour primary>>;
}

.xp-aphorism>tbody>.xp-aphorism-item:nth-child(odd) {
    background: <<colour code-background>>;
}

.xp-aphorism>tbody>.xp-aphorism-item:nth-child(even) {
    background-color: <<colour background>>;
}

.xp-aphorism-controls {
    display: none;
    padding-right: .5em;
    transition: opacity .5s ease-in-out;
}

.xp-aphorism-item:hover .xp-aphorism-controls {
    display: block;
}

.xp-aphorism-edit-frame .tc-editor-toolbar {
    z-index: 499 !important;
}


